    background-color: rgba($color-white,0.8);

    cursor: pointer;
    transition: all 0.2s;

    &:hover {
        transform: translateY(-2px);
        box-shadow: 0 0.1rem 2rem rgba($color-white,0.3); 

    }

    &:active {
        transform: translateY(-1px);
        box-shadow: 0 0.1rem 0.5rem rgba($color-white,0.3); 
    }
}

