sources.sass/components/_modal.scss:
.houseModal {
    height: 100vh;
    width: 100%;
    position: fixed;
    top: 0%;
    left: 0%;
    background-color: rgba($color-black, .8); // make sure no space after rgba
    z-index: 999999;


    &__content {
        @include centerItem;
        height: 40vh;
        width: 40vw;
        text-align: center;
        background-color: rgba($color-grey-light-2, .3);
        border-radius: 0.4rem;
        overflow: hidden;
        display: flex;
        flex-direction: column;

    }

    &__header {
        flex-basis: 15%;
        padding: 1.5rem 0;
        background-color: $color-primary;

    }

    &__body {
        flex-grow: 1;

        &__question {
            width: 100%;
            @include centerItem;



            &--1 {


                span {
                    display: block;
                    width: 100%;
                    padding: 3rem 0;
                    font-size: 2.5rem;
                }
                a {                   
                    margin: 0 1rem;
                    @include button;
                }   
            }

            &--2, &--3, &--4, &--5, &--6 {
                display: flex;
                flex-direction: column;
                align-items: center;
                opacity: 0;
                visibility: hidden;

                .disabled {
                    pointer-events: none;
                }

                span {
                    display: block;
                    font-size: 2rem;
                    margin-top: 4rem;
                }

                .inputContainer {
                    display: flex;
                    align-items: center;

                    span {
                        background-color: $color-grey-light-1;
                        margin: 0;
                        color: $color-grey-dark-3;
                        font-size: 2rem;
                        transform: translateX(4.2rem) translateY(2px);
                    }
                }

                input {
                    width: 25rem;
                    font-family: inherit;
                    font-size: 2rem;
                    text-align: center;
                    color: $color-grey-dark-3;
                    background-color: $color-grey-light-1;
                    border-radius: 0.3rem;
                    margin: 2rem auto;
                    padding: 1rem 2rem;
                    outline: none;
                    border: none;
                    display: block;
                }

                //remove the scroller from input tag
                input::-webkit-outer-spin-button,
                input::-webkit-inner-spin-button {
                -webkit-appearance: none;
                margin: 0;
                }

                .button {
                    margin-top: 1rem;
                    @include button;
                    &:focus{
                        outline: none;
                        transform: translateY(-2px);
                        box-shadow: 0 0.1rem 2rem rgba($color-white,0.3); 
                    }
                }    
            } 


        }
    }
}


:target {
    opacity: 1;
    visibility: visible;
}

sources.sass/_mixin.scss:
@mixin centerItem {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}


@mixin button{
    font-size: 1.5rem;
    color: $color-grey-dark-3;

    text-transform: uppercase;
    text-decoration: none;
    display: inline-block;
    padding: 1.5rem 4rem;
    border-radius: 10rem; 
    background-color: rgba($color-white,0.8);

    cursor: pointer;
    transition: all 0.2s;

    &:hover {
        transform: translateY(-2px);
        box-shadow: 0 0.1rem 2rem rgba($color-white,0.3); 

    }

    &:active {
        transform: translateY(-1px);
        box-shadow: 0 0.1rem 0.5rem rgba($color-white,0.3); 
    }
}

